import React, {useState, useContext} from 'react'
import axios from 'axios'
import {GlobalState} from '../../../../GlobalState'
import Loading from '../utils/loading/Loading'

const initialState = {
  product_id: '',
  title: '',
  price: 0,
  description: 'garcha',
  content: "poronga",
  category: ''
}

function CreateProduct() {
  const state = useContext(GlobalState)
  const [product, setProduct] = useState(initialState)
  const [categories] = state.categoriesAPI.categories
  const [images, setImages] = useState(false)
  const [loading, setLoading] = useState(false) 



  const [isAdmin] = state.userAPI.isAdmin
  const handleUpload = async e =>{
    e.preventDefault()
    try {
      if(!isAdmin) return alert("no sos admin, cap :/")
      const file = e.target.files[0]

      if(!file) if(!isAdmin) return alert("ese archivo no existe :/")

      if(file.size > 1024 * 1024) 
      return alert("archivo muy extenso :/")
      if(file.type !== 'image/jpeg' && file.type !== 'image/png') 
      return alert("formato de archivo equivocado :/")


    } catch (err) {
      alert(err.response.data.msg)
    }
  }

  const styleUpload = {
    display: images ? "block" : "none"
  }
  return (
    <div className='create_product'>
      <div className="upload">
        <input type="file" name="file" id="file_up" onChange={handleUpload}/>
        <div id="file_img" style={styleUpload}>
          <img src="" alt="" />
          <span>X</span>
        </div>
      </div>
      <form>
        <div className="row">
          <label htmlFor="product_id">Product ID</label>
          <input type="text" name="product_id" id="product_id"
          required value={product.product_id} />
        </div>

        <div className="row">
          <label htmlFor="title">Title</label>
          <input type="text" name="title" id="title"
          required value={product.title} />
        </div>

        <div className="row">
          <label htmlFor="price">price</label>
          <input type="number" name="price" id="price"
          required value={product.price} />
        </div>

        <div className="row">
          <label htmlFor="description">description</label>
          <textarea type="text" name="description" id="description"
          required value={product.description} rows="5"/>
        </div>

        <div className="row">
          <label htmlFor="content">content</label>
          <textarea type="text" name="content" id="content"
          required value={product.content} rows="7"/>
        </div>

        <div className="row">
          <label htmlFor="categories">categories: </label>
          <select name="category" value={product.category}>
            <option value="">
              {
                categories.map(category =>(
                  <option value={category._id} key={category._id}> 
                    {category.name}
                  </option>
                ))
              }
            </option>
          </select>
        </div>
        <button type='submit'>Create</button>
      </form>
    </div>
  )
}

export default CreateProduct
